import React, { useState } from "react";
import FormResult from "./FormResult";

const UserForm = (props) => {
  const [onChangeName, setOnChangeName] = useState("");
  const [onChangeAge, setOnChangeAge] = useState("");

  const inputNameChangeHandler = (e) => {
    setOnChangeName(e.target.value);
  };
  const inputAgeChangeHandler = (e) => {
    setOnChangeAge(e.target.value);
  };
  const onSubmitHandler = (e) => {
    e.preventDefault();
    if(onChangeName.trim)
    console.log(onChangeName, onChangeAge);
    const expenseData = {
      name: onChangeName,
      age: onChangeAge,
    };

    // props.saveDataInput(expenseData);
    setOnChangeName("");
    setOnChangeAge("");
  };

  return (
    <div>
      <form onSubmit={onSubmitHandler}>
        <label>Enter user name</label>
        <input
          value={onChangeName}
          type="text"
          onChange={inputNameChangeHandler}
        ></input>
        <label>Enter user age</label>
        <input
          value={onChangeAge}
          type="text"
          onChange={inputAgeChangeHandler}
        ></input>
        <button type="submit">Add user</button>
      </form>
    </div>
  );
};

export default UserForm;
